// This is the page where the user is directed after logging in.
import './main.css';
import volumefy from "./volumefy.png";
import { Link } from "react-router-dom";
import * as React from "react";
import ReactDOM from "react";
import $ from 'jquery';
const jwt = require('jsonwebtoken');


const Main = () => {
  const testToken = () => {
    var response = localStorage.getItem("response");
    console.log(response);
    response = jwt.decode(response);
    console.log("decoded response:", response);
    console.log("username", response.username);
    let h1 = React.createElement("h1", null, "yarrak");
    ReactDOM.render(h1, document.getElementById("main"));
  }

  return (
    <div className="Main">

      {/* Name and logo */}
        <div id = "main">
            <img src={volumefy} className="logo"/>
            <h1> VOLUMEFY</h1><hr/>
        </div>
        <h2 class="test">deneme</h2>
        <h2>Welcome to Volumefy</h2><br/>
        <button onClick={testToken}>TEST</button>
        <button><Link to="/">SIGN Out</Link></button>
    </div>
  );
}

export default Main;
