// This is the page where the user is directed after logging in.
import './main.css';
import volumefy from "./volumefy.png";
import { Link, useHistory } from "react-router-dom";
import * as React from "react";
import ReactDOM from "react-dom";
import $ from 'jquery';
// import bg from "./images/crowd.jpg"
const jwt = require('jsonwebtoken');



const Main = () => {

  document.body.style.backgroundImage = "url(./images/crowd.jpg)";

  let history = useHistory();

  // On load, get the token from the local storage and get
  // the usarname from it. Then, create a h2 element with
  // that usarname.
  React.useEffect(() => {
    var response = localStorage.getItem("response");
    console.log(response);
    response = jwt.decode(response);
    response = response.username;
    let h_element = React.createElement("h2", null, "welcome back ", response, "!");
    ReactDOM.render(h_element, document.getElementById("right"));
  })

  const logOut = () => {
    localStorage.clear();
    history.push("/");
  };

  return (
    <div className="Main" >
      {/* Name and logo */}
        <div id = "center">
            <img src={volumefy} className="logo"/>
            <h1> VOLUMEFY</h1><hr/>
        </div>
        <div id = "right" className = "right"></div><br/>
        <div id = "left" className = "left"></div><br/>
        <button onClick={logOut}><Link to="/">SIGN Out</Link></button>
    </div>
  );
}

export default Main;
