// This is the page where the user is directed after logging in.
import './main.css';
import volumefy from "./volumefy.png";
import { Link } from "react-router-dom";
import * as React from "react";
import ReactDOM from "react-dom";
import $ from 'jquery';
const jwt = require('jsonwebtoken');


const Main = () => {

  // On load, get the token from the local storage and get
  // the usarname from it. Then, create a h2 element with
  // that usarname.
  React.useEffect(() => {
    var response = localStorage.getItem("response");
    console.log(response);
    response = jwt.decode(response);
    response = response.username;
    let test = React.createElement("h2", null, "welcome back ", response, "!");
    ReactDOM.render(test, document.getElementById("main"));
  })

  return (
    <div className="Main">

      {/* Name and logo */}
        <div>
            <img src={volumefy} className="logo"/>
            <h1> VOLUMEFY</h1><hr/>
        </div>
        <div id = "main"></div>
        <h2>Welcome to Volumefy</h2><br/>
        <button><Link to="/">SIGN Out</Link></button>
    </div>
  );
}

export default Main;
